import React from 'react';
import { BsArrowUpRight } from 'react-icons/bs';
import { motion } from 'framer-motion';
import { fadeIn } from '../variants'

const services = [
  {
    name:'UI/UX Design',
    description:
     'Enim mollit amet velit consectetur sit voluptate consectetur deserunt sint labore reprehenderit. Consequat velit aliqua fugiat Lorem. Sint occaecat consequat esse non aliqua veniam. Ad dolore cillum culpa quis non mollit officia.',
    link: 'Learn more',
  },
  {
    name:'Development',
    description:
     'Enim mollit amet velit consectetur sit voluptate consectetur deserunt sint labore reprehenderit. Consequat velit aliqua fugiat Lorem. Sint occaecat consequat esse non aliqua veniam. Ad dolore cillum culpa quis non mollit officia.',
    link: 'Learn more',
  },
  {
    name:'Digital Marketing',
    description:
     'Enim mollit amet velit consectetur sit voluptate consectetur deserunt sint labore reprehenderit. Consequat velit aliqua fugiat Lorem. Sint occaecat consequat esse non aliqua veniam. Ad dolore cillum culpa quis non mollit officia.',
    link: 'Learn more',
  }
]


const Services = () => {
  return (
  <section className='section'  id='services'>
    <div className='container mx-auto'>
      <div className='flex flex-col lg:flex-row'>
        {/*text & image*/}
        <div className='flex-1 lg:bg-services lg:bg-bottom bg-no-repeat mix-blend-lighten mb-12 lg:mb-0'>
          <h2 className='h2 text-accent mb-6'>What I Do.</h2>
          <h3 className='h3 max-w-[455px] mb-16'>
            I'm a Freelance Front-end Developer with over 5 years of experience.
          </h3> 
          <button className='btn btn-sm'>See my work</button>
        </div>
        {/*services */}
        <div>
        {/*services */}
        <div>
          {services.map((service, index) => {
            // destructure service
            const {name, description, link} = service;
            return (
            <div className='border-b border-white/20 h-[146px] mb-[38px] flex' key={index}>
              <div className='max-w-[476px]'>
                <h4 className='text-[20px] tracking-wider font-primary font-semibold mb-6'>{name}</h4>
                <p className='font-secondary leading-tight'>
                  {description}
                </p>
              </div>
              <div className='flex flex-col flex-1 items-end'>
               <a href="#" className='btn w-p h-9 mb-[42px] flex justify-center items-center'>
                <BsArrowUpRight/> 
              </a>
              <a href='#' className='text-gradient text-sm'>
                {link}
                </a>
              </div>
            </div>
            );
          })}
        </div>
        </div>
      </div>
    </div>

  </section>
  )
};

export default Services;
