    //     blob.animate({
    //         left:`${clientX}px`,
    //         top: `${clientY}px`,
    //     }, { duration: 3000, fill: "forwards"
    //     });
    // }
  return (
    
    <div id="bgblob" ></div>
    
  )
}

export default BGBlob
